mapper.update:
update bd_casecheck_noticeread
set gjpcggid = #{gjpcggid,jdbcType=VARCHAR},
  ydr = #{ydr,jdbcType=VARCHAR},
  ydsj = #{ydsj,jdbcType=VARCHAR}
where ajpcggydid = #{ajpcggydid,jdbcType=VARCHAR}

mapper.select:
select ajpcggydid, gjpcggid, ydr, ydsj
from bd_casecheck_noticeread
where ajpcggydid = #{ajpcggydid,jdbcType=VARCHAR}
