set sjpxggid = #{sjpxggid,jdbcType=VARCHAR},
  ydr = #{ydr,jdbcType=VARCHAR},
  ydsj = #{ydsj,jdbcType=TIMESTAMP}
where sjpxggydid = #{sjpxggydid,jdbcType=VARCHAR}

mapper.select:
