mapper.select:
select a.ccid, a.checkid, a.ajid, a.psgroupid, a.jydp, a.zzpcdf, a.zzzldj,a.dpr,date_format(a.dpsj, '%Y/%m/%d') dpsj, a.create_by, date_format(a.create_date, '%Y/%m/%d') create_date, a.update_by, date_format(a.update_date, '%Y/%m/%d') update_date, a.remarks, a.flag, b.bt ggmc, (select u.xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz = '1' limit 1) teamleadername, (select GROUP_CONCAT(u.xm SEPARATOR ';') xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz != '1' GROUP BY t.groupid limit 1) teammatenames ,c.ah,c.gsfy,c.cbbm,c.cbr,c.ay,c.xz,c.lx,c.jafs,DATE_FORMAT(c.jasj, '%Y/%m/%d') jasj,c.relatedcasecount,(select count(1) from bd_check_cases x where x.ajid = a.ajid and x.jydp is not null) pcjl from bd_check_cases a,bd_check b,bd_middle_case c where a.checkid = b.checkid and a.ajid = c.ajid
and a.ajid = #{ajid}
and a.checkid = #{checkid}
and a.jydp is not null
