mapper.select:
select b.bt,date_format(b.fqsj, '%Y/%m/%d') fqsj,b.pclx, b.pcrw,fn_get_username(b.fqr) fqrmc
,a.ccid, a.checkid, a.ajid, a.psgroupid, a.jydp, a.zzpcdf, a.zzzldj,a.dpr,date_format(a.dpsj, '%Y/%m/%d') dpsj, a.create_by,
date_format(a.create_date, '%Y/%m/%d') create_date, a.update_by, date_format(a.update_date, '%Y/%m/%d') update_date, a.remarks, a.flag,
b.bt ggmc,
(select u.xm  from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz = '1' limit 1) teamleadername,
(select GROUP_CONCAT(u.xm SEPARATOR ';') xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz != '1' GROUP BY t.groupid limit 1) teammatenames
,c.ah,c.gsfy,c.cbbm,c.cbr,c.ay,c.xz,c.lx,c.jafs,DATE_FORMAT(c.jasj, '%Y/%m/%d') jasj,c.relatedcasecount,(select count(1) from bd_check_cases x where x.ajid = a.ajid and x.jydp is not null) pcjl
from bd_check_cases a,bd_check b,bd_middle_case c,sd_user u,bd_check_groups g where a.checkid = b.checkid and a.ajid = c.ajid and b.fqr = u.yhid and a.psgroupid = g.cgid

  and a.ajid = #{ajid}


  and a.checkid = #{checkid}


  and a.jydp is not null


  and c.gsfy like concat('%',#{gsfy},'%')


  and c.cbbm like concat('%',#{cbbm},'%')


  and c.cbr like concat('%',#{cbr},'%')


  and c.ah like concat('%',#{ah},'%')


  and c.xz = #{xz}


  and c.lx = #{lx}


  and a.psgroupid = #{psgroupid}


  and b.fqr = #{fqr}


  and b.bt like concat('%',#{bt},'%')


  and b.pclx = #{pclx}


  and b.pcrw = #{pcrw}


  and u.xm like concat('%',#{fqrmc},'%')


  and g.tdcy like concat('%',#{tdcy},'%')



  and date_format(b.fqsj, '%Y/%m/%d') >= #{fqsj1}


  and date_format(b.fqsj, '%Y/%m/%d') <= #{fqsj2}

order by b.bt asc ,a.zzpcdf desc, a.create_date desc

select CONCAT(a.ccid,p.cpid) keyid,p.sfzz,b.bt,date_format(b.fqsj, '%Y/%m/%d') fqsj,b.pclx, b.pcrw,fn_get_username(b.fqr) fqrmc ,a.ccid, a.checkid, a.ajid, a.psgroupid, a.jydp, a.zzpcdf, a.zzzldj,a.dpr,date_format(a.dpsj, '%Y/%m/%d') dpsj, a.create_by, date_format(a.create_date, '%Y/%m/%d') create_date, a.update_by, date_format(a.update_date, '%Y/%m/%d') update_date, a.remarks, a.flag, b.bt ggmc, (select u.xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz = '1' limit 1) teamleadername, (select GROUP_CONCAT(u.xm SEPARATOR ';') xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz != '1' GROUP BY t.groupid limit 1) teammatenames ,c.ah,c.gsfy,c.cbbm,c.cbr,c.ay,c.xz,c.lx,c.jafs,DATE_FORMAT(c.jasj, '%Y/%m/%d') jasj,c.relatedcasecount,(select count(1) from bd_check_cases x where x.ajid = a.ajid and x.jydp is not null) pcjl from bd_check_cases a left join bd_check b on a.checkid = b.checkid left join bd_middle_case c on a.ajid = c.ajid left join bd_check_pros p on a.psgroupid = p.groupid and a.checkid = p.checkid left join bd_check_records r on r.checkid = a.checkid and r.ajid = a.ajid and r.pcr = p.proid left join sd_user u on b.fqr = u.yhid where b.zt = '1'
and p.proid = #{proid}
and b.bt like concat('%',#{bt},'%')
and b.pclx = #{pclx}
and b.pcrw = #{pcrw}
and u.xm like concat('%',#{fqrmc},'%')
and date_format(b.fqsj, '%Y/%m/%d') >= #{fqsj1}
and date_format(b.fqsj, '%Y/%m/%d') <= #{fqsj2}
and ((p.sfzz = '1' and a.jydp is null) or (r.pczt is null or r.pczt = '1'))
and r.pczt = '2'
order by b.bt asc , a.create_date desc

select b.bt,date_format(b.fqsj, '%Y/%m/%d') fqsj,b.pclx, b.pcrw,fn_get_username(b.fqr) fqrmc, a.checkid, a.ajid, a.psgroupid, a.jydp, a.zzpcdf, a.zzzldj,a.dpr,date_format(a.dpsj, '%Y/%m/%d') dpsj, a.create_by, date_format(a.create_date, '%Y/%m/%d') create_date, a.update_by, date_format(a.update_date, '%Y/%m/%d') update_date, a.remarks, a.flag, (select u.xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz = '1' limit 1) teamleadername, (select GROUP_CONCAT(u.xm SEPARATOR ';') xm from bd_check_pros t,sd_user u where t.proid =u.yhid and t.checkid = a.checkid and t.groupid = a.psgroupid and t.sfzz != '1' GROUP BY t.groupid limit 1) teammatenames, c.ah,c.gsfy,c.cbbm,c.cbr,c.ay,c.xz,c.lx,c.jafs,DATE_FORMAT(c.jasj, '%Y/%m/%d') jasj from bd_check_cases a , bd_check b ,bd_middle_case c where a.checkid = b.checkid and a.ajid = c.ajid
and date_format(b.fqsj, '%Y/%m/%d') >= #{fqsj1}
and date_format(b.fqsj, '%Y/%m/%d') <= #{fqsj2}
order by a.zzpcdf desc , a.create_date desc
