mapper.select:
select checkid, bt, btys, pclx, pcrw, pfb, fqr,fn_get_username(fqr) fqrmc, date_format(fqsj, '%Y/%m/%d') fqsj, shr,fn_get_username(shr) shrmc, date_format(shsj, '%Y/%m/%d') shsj, shyj, zt, create_by, date_format(create_date, '%Y/%m/%d') create_date, update_by, date_format(update_date, '%Y/%m/%d') update_date, remarks, flag, nr from bd_check where 1=1
and zt = #{zt}
and shr = #{shrmc}
order by create_date desc
