mapper.select:
select t.chosenid, t.bt, t.btys, t.pclx, t.pfb,date_format(t.jzrq, '%Y/%m/%d') jzrq, (select a.mbmc from bd_scoretables a where a.tableid = t.pfb limit 1) pfbmc, t.fqr,fn_get_username(t.fqr) fqrmc, (select oi.zzjgmc from sd_user us,sd_organization oi where us.zzid = oi.zzjgid and us.yhid =t.fqr limit 1) orgname, date_format(t.fqsj, '%Y/%m/%d') fqsj, shr,fn_get_username(shr) shrmc, date_format(shsj, '%Y/%m/%d') shsj, t.shyj, t.zt, t.create_by, date_format(t.create_date, '%Y/%m/%d') create_date, t.update_by, date_format(t.update_date, '%Y/%m/%d') update_date, t.remarks, t.flag, t.nr ,(select count(1) from bd_chosen_read x where x.readman=#{userid} and x.chosenid = t.chosenid) sfyd, (select GROUP_CONCAT(m.proname SEPARATOR ';') xm from bd_chosen_msgto m where m.chosenid = t.chosenid group by m.chosenid limit 1) msgtonames ,(select count(1) from bd_chosen_cases x where x.chosenid = t.chosenid) ytbajs from bd_chosen t,sd_user u where t.fqr = u.yhid
and t.zt = #{zt}
and t.shr = #{shrmc}
and t.pclx = #{pclx}
and t.bt like concat('%',#{bt},'%')
and u.xm like concat('%',#{fqrmc},'%')
and date_format(t.fqsj, '%Y/%m/%d') >= #{fqsj1}
and date_format(t.fqsj, '%Y/%m/%d') <= #{fqsj2}
order by create_date desc
