mapper.select:
select bp.xxid, bp.bt, bp.btys, bp.xxlx,fn_get_dict(bp.xxlx) xxlxmc, xxnr, create_by,fn_get_username(create_by) create_bymc , DATE_FORMAT(create_date, '%Y/%m/%d %H:%i:%S') create_date, remarks,fn_get_dict(remarks) ztmc ,(select fn_get_username(sfh.shr) from sd_flow_history sfh where sfh.lcmk='10303' and sfh.shnrid = bp.xxid order by sfh.create_date desc limit 1) shr ,(select sfh.shyj from sd_flow_history sfh where sfh.lcmk='10303' and sfh.shnrid = bp.xxid order by sfh.create_date desc limit 1) shyj ,(select DATE_FORMAT(sfh.shsj, '%Y/%m/%d %H:%i:%S') from sd_flow_history sfh where sfh.lcmk='10303' and sfh.shnrid = bp.xxid order by sfh.create_date desc limit 1) shsj from bd_publish bp where 1=1
and bp.`bt` like concat('%',#{bt},'%')
and bp.xxlx = #{xxlx}
and date_format(createDate, '%Y/%m/%d %H:%i:%S') >= #{createdate1}
and date_format(createDate, '%Y/%m/%d %H:%i:%S') <= #{createdate2}
order by create_date DESC
