mapper.update:
update bd_publish_read
set xxggid = #{xxggid,jdbcType=VARCHAR},
  ydr = #{ydr,jdbcType=VARCHAR},
  ydsj = #{ydsj,jdbcType=TIMESTAMP}
where xxggydid = #{xxggydid,jdbcType=VARCHAR}

mapper.select:
select xxggydid, xxggid, ydr, ydsj
from bd_publish_read
where xxggydid = #{xxggydid,jdbcType=VARCHAR}
