mapper.update:
update WS_AJID
set CREATETIME = #{createtime,jdbcType=TIMESTAMP},
  UPDATETIME = #{updatetime,jdbcType=TIMESTAMP},
  FJM = #{fjm,jdbcType=VARCHAR}
where TDHAJID = #{tdhajid,jdbcType=VARCHAR}

mapper.select:
select TDHAJID, CREATETIME, UPDATETIME, FJM
from WS_AJID
where TDHAJID = #{tdhajid,jdbcType=VARCHAR}
