mapper.update:
update WS_LOG
set taskid = #{taskid,jdbcType=VARCHAR},
  taskitemid = #{taskitemid,jdbcType=VARCHAR},
  log = #{log,jdbcType=LONGVARCHAR}
where errorid = #{errorid,jdbcType=VARCHAR}

mapper.select:
select errorid, taskid, taskitemid, log
from WS_LOG
where errorid = #{errorid,jdbcType=VARCHAR}
