mapper.update:
update WS_TASK_ITEMS
set taskid = #{taskid,jdbcType=VARCHAR},
  params = #{params,jdbcType=VARCHAR},
  datacount = #{datacount,jdbcType=INTEGER},
  results = #{results,jdbcType=LONGVARCHAR}
where taskitemid = #{taskitemid,jdbcType=VARCHAR}

mapper.select:
select taskitemid, taskid, params, datacount, results
from WS_TASK_ITEMS
where taskitemid = #{taskitemid,jdbcType=VARCHAR}
