mapper.update:
update WS_TASK
set tasktype = #{tasktype,jdbcType=VARCHAR},
  begeindate = #{begeindate,jdbcType=TIMESTAMP},
  enddate = #{enddate,jdbcType=TIMESTAMP}
where taskid = #{taskid,jdbcType=VARCHAR}

mapper.select:
select taskid, tasktype, begeindate, enddate
from WS_TASK
where taskid = #{taskid,jdbcType=VARCHAR}
